'use client'

import { useState, useEffect } from 'react'
import { useAuth } from '@/providers/auth-provider'
import { mockUsers, mockDoctors, mockClinics, User, Doctor, Clinic } from '@/lib/mock-data'
import { PageLoadingSpinner } from '@/components/loading-spinner'
import { Navbar } from '@/components/navbar'
import { BreadcrumbNav } from '@/components/breadcrumb-nav'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { Calendar, Clock, MapPin, Stethoscope, Building2, Phone, User as UserIcon } from 'lucide-react'
import { toast } from 'sonner'

interface Appointment {
  id: string
  patient_id: string
  doctor_id?: string
  clinic_id?: string
  appointment_date: string
  appointment_time: string
  status: 'pending' | 'confirmed' | 'completed' | 'cancelled'
  notes?: string
  created_at: string
  patient?: User
  doctor?: Doctor
  clinic?: Clinic
}

export default function AppointmentsPage() {
  const { user, userRoles, loading: authLoading } = useAuth()
  const [appointments, setAppointments] = useState<Appointment[]>([])
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    if (user) {
      loadAppointments()
    }
  }, [user, userRoles])

  const loadAppointments = () => {
    // Simulate loading appointments
    setTimeout(() => {
      const mockAppointments: Appointment[] = []

      if (userRoles.includes('patient')) {
        mockAppointments.push(
          {
            id: 'apt1',
            patient_id: user!.id,
            doctor_id: 'doctor1',
            appointment_date: '2024-01-25',
            appointment_time: '10:00',
            status: 'confirmed',
            notes: 'Regular checkup',
            created_at: '2024-01-20T00:00:00Z',
            patient: mockUsers.find(u => u.id === user!.id),
            doctor: mockDoctors.find(d => d.user_id === 'doctor1')
          },
          {
            id: 'apt2',
            patient_id: user!.id,
            clinic_id: 'clinic1',
            appointment_date: '2024-01-28',
            appointment_time: '14:30',
            status: 'pending',
            notes: 'Blood test',
            created_at: '2024-01-22T00:00:00Z',
            patient: mockUsers.find(u => u.id === user!.id),
            clinic: mockClinics.find(c => c.user_id === 'clinic1')
          }
        )
      }

      if (userRoles.includes('doctor')) {
        mockAppointments.push(
          {
            id: 'apt3',
            patient_id: 'patient1',
            doctor_id: user!.id,
            appointment_date: '2024-01-25',
            appointment_time: '09:00',
            status: 'confirmed',
            notes: 'Follow-up consultation',
            created_at: '2024-01-20T00:00:00Z',
            patient: mockUsers.find(u => u.id === 'patient1'),
            doctor: mockDoctors.find(d => d.user_id === user!.id)
          },
          {
            id: 'apt4',
            patient_id: 'patient2',
            doctor_id: user!.id,
            appointment_date: '2024-01-26',
            appointment_time: '11:30',
            status: 'pending',
            notes: 'Initial consultation',
            created_at: '2024-01-23T00:00:00Z',
            patient: mockUsers.find(u => u.id === 'patient2'),
            doctor: mockDoctors.find(d => d.user_id === user!.id)
          }
        )
      }

      if (userRoles.includes('clinic')) {
        mockAppointments.push(
          {
            id: 'apt5',
            patient_id: 'patient1',
            clinic_id: user!.id,
            appointment_date: '2024-01-27',
            appointment_time: '15:00',
            status: 'confirmed',
            notes: 'Lab work',
            created_at: '2024-01-21T00:00:00Z',
            patient: mockUsers.find(u => u.id === 'patient1'),
            clinic: mockClinics.find(c => c.user_id === user!.id)
          }
        )
      }

      setAppointments(mockAppointments)
      setLoading(false)
    }, 500)
  }

  const updateAppointmentStatus = (appointmentId: string, newStatus: Appointment['status']) => {
    setAppointments(prev => prev.map(apt =>
      apt.id === appointmentId ? { ...apt, status: newStatus } : apt
    ))

    const statusMessages = {
      confirmed: 'Appointment confirmed',
      completed: 'Appointment marked as completed',
      cancelled: 'Appointment cancelled'
    }

    toast.success(statusMessages[newStatus] || 'Appointment updated')
  }

  const getStatusColor = (status: Appointment['status']) => {
    switch (status) {
      case 'confirmed': return 'bg-green-100 text-green-800'
      case 'pending': return 'bg-yellow-100 text-yellow-800'
      case 'completed': return 'bg-blue-100 text-blue-800'
      case 'cancelled': return 'bg-red-100 text-red-800'
      default: return 'bg-gray-100 text-gray-800'
    }
  }

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    })
  }

  const formatTime = (timeString: string) => {
    return new Date(`2000-01-01T${timeString}`).toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    })
  }

  if (authLoading || loading) {
    return <PageLoadingSpinner />
  }

  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4">Access Denied</h1>
          <p className="text-muted-foreground">Please login to view appointments.</p>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-indigo-50 to-white">
      <Navbar />

      <div className="container py-8">
        <BreadcrumbNav items={[
          { label: 'My Appointments' }
        ]} />

        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center space-x-3 mb-4">
            <div className="w-12 h-12 bg-indigo-100 rounded-full flex items-center justify-center">
              <Calendar className="h-6 w-6 text-indigo-600" />
            </div>
            <div>
              <h1 className="text-3xl font-bold">My Appointments</h1>
              <p className="text-muted-foreground">
                {userRoles.includes('patient') && 'View and manage your medical appointments'}
                {userRoles.includes('doctor') && 'Manage your patient appointments'}
                {userRoles.includes('clinic') && 'Manage your clinic appointments'}
              </p>
            </div>
          </div>
        </div>

        {/* Appointments List */}
        <div className="space-y-6">
          {appointments.map((appointment) => (
            <Card key={appointment.id} className="hover:shadow-lg transition-shadow">
              <CardContent className="p-6">
                <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between space-y-4 lg:space-y-0">
                  <div className="flex-1">
                    <div className="flex items-center space-x-3 mb-3">
                      <div className="w-12 h-12 bg-primary/10 rounded-full flex items-center justify-center">
                        {appointment.doctor_id ? (
                          <Stethoscope className="h-6 w-6 text-blue-600" />
                        ) : (
                          <Building2 className="h-6 w-6 text-purple-600" />
                        )}
                      </div>
                      <div>
                        <h3 className="text-lg font-semibold">
                          {appointment.doctor_id 
                            ? appointment.doctor?.user?.full_name 
                            : appointment.clinic?.name
                          }
                        </h3>
                        <p className="text-sm text-muted-foreground">
                          {appointment.doctor_id 
                            ? appointment.doctor?.specialization
                            : 'Medical Clinic'
                          }
                        </p>
                      </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                      <div className="flex items-center space-x-2">
                        <Calendar className="h-4 w-4 text-muted-foreground" />
                        <span>{formatDate(appointment.appointment_date)}</span>
                      </div>

                      <div className="flex items-center space-x-2">
                        <Clock className="h-4 w-4 text-muted-foreground" />
                        <span>{formatTime(appointment.appointment_time)}</span>
                      </div>

                      {(userRoles.includes('doctor') || userRoles.includes('clinic')) && (
                        <div className="flex items-center space-x-2">
                          <UserIcon className="h-4 w-4 text-muted-foreground" />
                          <span>{appointment.patient?.full_name}</span>
                        </div>
                      )}

                      {appointment.clinic && (
                        <div className="flex items-center space-x-2">
                          <MapPin className="h-4 w-4 text-muted-foreground" />
                          <span className="truncate">{appointment.clinic.address}</span>
                        </div>
                      )}
                    </div>

                    {appointment.notes && (
                      <div className="mt-3 p-3 bg-muted/50 rounded-lg">
                        <p className="text-sm"><strong>Notes:</strong> {appointment.notes}</p>
                      </div>
                    )}
                  </div>

                  <div className="flex flex-col items-end space-y-3">
                    <Badge className={getStatusColor(appointment.status)}>
                      {appointment.status.charAt(0).toUpperCase() + appointment.status.slice(1)}
                    </Badge>

                    {/* Action buttons based on user role and appointment status */}
                    <div className="flex space-x-2">
                      {userRoles.includes('patient') && appointment.status === 'confirmed' && (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => updateAppointmentStatus(appointment.id, 'cancelled')}
                        >
                          Cancel
                        </Button>
                      )}

                      {(userRoles.includes('doctor') || userRoles.includes('clinic')) && (
                        <>
                          {appointment.status === 'pending' && (
                            <Button
                              size="sm"
                              onClick={() => updateAppointmentStatus(appointment.id, 'confirmed')}
                            >
                              Confirm
                            </Button>
                          )}

                          {appointment.status === 'confirmed' && (
                            <Button
                              size="sm"
                              onClick={() => updateAppointmentStatus(appointment.id, 'completed')}
                            >
                              Complete
                            </Button>
                          )}

                          {appointment.status !== 'completed' && appointment.status !== 'cancelled' && (
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => updateAppointmentStatus(appointment.id, 'cancelled')}
                            >
                              Cancel
                            </Button>
                          )}
                        </>
                      )}
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {appointments.length === 0 && (
          <div className="text-center py-12">
            <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
              <Calendar className="h-8 w-8 text-muted-foreground" />
            </div>
            <h3 className="text-lg font-medium mb-2">No appointments found</h3>
            <p className="text-muted-foreground mb-4">
              {userRoles.includes('patient') 
                ? "You don't have any appointments scheduled yet."
                : "No appointments scheduled with you yet."
              }
            </p>
            {userRoles.includes('patient') && (
              <div className="space-x-2">
                <Button asChild>
                  <a href="/doctors">Book with Doctor</a>
                </Button>
                <Button asChild variant="outline">
                  <a href="/clinics">Book with Clinic</a>
                </Button>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  )
}

app/chats/page.tsx.content:
'use client'

import { useState, useEffect } from 'react'
import { useAuth } from '@/providers/auth-provider'
import { mockUsers, User } from '@/lib/mock-data'
import { PageLoadingSpinner } from '@/components/loading-spinner'
import { Navbar } from '@/components/navbar'
import { BreadcrumbNav } from '@/components/breadcrumb-nav'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import { MessageCircle, Search, Send, User as UserIcon, Stethoscope, Building2 } from 'lucide-react'
import { toast } from 'sonner'

interface Chat {
  id: string
  participant: User
  lastMessage: string
  lastMessageTime: string
  unreadCount: number
}

interface Message {
  id: string
  senderId: string
  content: string
  timestamp: string
}

export default function ChatsPage() {
  const { user, userRoles, loading: authLoading } = useAuth()
  const [chats, setChats] = useState<Chat[]>([])
  const [selectedChat, setSelectedChat] = useState<Chat | null>(null)
  const [messages, setMessages] = useState<Message[]>([])
  const [newMessage, setNewMessage] = useState('')
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    if (user) {
      loadChats()
    }
  }, [user])

  const loadChats = () => {
    // Simulate loading chats
    setTimeout(() => {
      const mockChats: Chat[] = []

      // Add some sample chats based on user role
      if (userRoles.includes('patient')) {
        mockChats.push({
          id: 'chat1',
          participant: mockUsers.find(u => u.id === 'doctor1')!,
          lastMessage: 'Your test results look good. Let me know if you have any questions.',
          lastMessageTime: '2 hours ago',
          unreadCount: 1
        })
        mockChats.push({
          id: 'chat2',
          participant: mockUsers.find(u => u.id === 'clinic1')!,
          lastMessage: 'Your appointment is confirmed for tomorrow at 2 PM.',
          lastMessageTime: '1 day ago',
          unreadCount: 0
        })
      }

      if (userRoles.includes('doctor')) {
        mockChats.push({
          id: 'chat3',
          participant: mockUsers.find(u => u.id === 'patient1')!,
          lastMessage: 'Thank you doctor, I feel much better now.',
          lastMessageTime: '30 minutes ago',
          unreadCount: 2
        })
        mockChats.push({
          id: 'chat4',
          participant: mockUsers.find(u => u.id === 'patient2')!,
          lastMessage: 'When should I take the medication?',
          lastMessageTime: '3 hours ago',
          unreadCount: 1
        })
      }

      setChats(mockChats)
      setLoading(false)
    }, 500)
  }

  const loadMessages = (chat: Chat) => {
    // Simulate loading messages for selected chat
    const mockMessages: Message[] = [
      {
        id: '1',
        senderId: chat.participant.id,
        content: 'Hello! How are you feeling today?',
        timestamp: '10:30 AM'
      },
      {
        id: '2',
        senderId: user!.id,
        content: 'Hi doctor, I\'m feeling much better after taking the medication.',
        timestamp: '10:35 AM'
      },
      {
        id: '3',
        senderId: chat.participant.id,
        content: 'That\'s great to hear! Continue taking the medication as prescribed.',
        timestamp: '10:40 AM'
      },
      {
        id: '4',
        senderId: chat.participant.id,
        content: chat.lastMessage,
        timestamp: chat.lastMessageTime
      }
    ]

    setMessages(mockMessages)
  }

  const selectChat = (chat: Chat) => {
    setSelectedChat(chat)
    loadMessages(chat)

    // Mark as read
    setChats(prev => prev.map(c => 
      c.id === chat.id ? { ...c, unreadCount: 0 } : c
    ))
  }

  const sendMessage = () => {
    if (!newMessage.trim() || !selectedChat) return

    const message: Message = {
      id: `msg_${Date.now()}`,
      senderId: user!.id,
      content: newMessage,
      timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    }

    setMessages(prev => [...prev, message])
    setNewMessage('')

    // Update last message in chat list
    setChats(prev => prev.map(chat =>
      chat.id === selectedChat.id
        ? { ...chat, lastMessage: newMessage, lastMessageTime: 'now' }
        : chat
    ))

    toast.success('Message sent')
  }

  const getUserIcon = (userId: string) => {
    const userRole = mockUsers.find(u => u.id === userId)
    if (userId === 'doctor1' || userId === 'doctor2') {
      return <Stethoscope className="h-4 w-4" />
    }
    if (userId === 'clinic1' || userId === 'clinic2') {
      return <Building2 className="h-4 w-4" />
    }
    return <UserIcon className="h-4 w-4" />
  }

  if (authLoading || loading) {
    return <PageLoadingSpinner />
  }

  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4">Access Denied</h1>
          <p className="text-muted-foreground">Please login to access chat.</p>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-teal-50 to-white">
      <Navbar />

      <div className="container py-8">
        <BreadcrumbNav items={[
          { label: 'Messages' }
        ]} />

        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center space-x-3 mb-4">
            <div className="w-12 h-12 bg-teal-100 rounded-full flex items-center justify-center">
              <MessageCircle className="h-6 w-6 text-teal-600" />
            </div>
            <div>
              <h1 className="text-3xl font-bold">Messages</h1>
              <p className="text-muted-foreground">Secure communication with healthcare providers</p>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-[600px]">
          {/* Chat List */}
          <Card className="lg:col-span-1">
            <CardHeader>
              <CardTitle>Conversations</CardTitle>
              <CardDescription>Your recent chats</CardDescription>
            </CardHeader>
            <CardContent className="p-0">
              <div className="space-y-1">
                {chats.map((chat) => (
                  <div
                    key={chat.id}
                    onClick={() => selectChat(chat)}
                    className={`p-4 cursor-pointer hover:bg-muted/50 transition-colors border-b ${
                      selectedChat?.id === chat.id ? 'bg-muted' : ''
                    }`}
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center">
                        {getUserIcon(chat.participant.id)}
                      </div>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center justify-between">
                          <h4 className="font-medium truncate">{chat.participant.full_name}</h4>
                          {chat.unreadCount > 0 && (
                            <Badge variant="default" className="ml-2">
                              {chat.unreadCount}
                            </Badge>
                          )}
                        </div>
                        <p className="text-sm text-muted-foreground truncate">
                          {chat.lastMessage}
                        </p>
                        <p className="text-xs text-muted-foreground">
                          {chat.lastMessageTime}
                        </p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {chats.length === 0 && (
                <div className="text-center py-8">
                  <MessageCircle className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                  <h3 className="font-medium mb-2">No conversations yet</h3>
                  <p className="text-sm text-muted-foreground">
                    Start chatting with healthcare providers
                  </p>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Chat Messages */}
          <Card className="lg:col-span-2">
            {selectedChat ? (
              <>
                <CardHeader className="border-b">
                  <div className="flex items-center space-x-3">
                    <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center">
                      {getUserIcon(selectedChat.participant.id)}
                    </div>
                    <div>
                      <CardTitle>{selectedChat.participant.full_name}</CardTitle>
                      <CardDescription>
                        {selectedChat.participant.email}
                      </CardDescription>
                    </div>
                  </div>
                </CardHeader>

                <CardContent className="flex flex-col h-[400px]">
                  {/* Messages */}
                  <div className="flex-1 overflow-y-auto space-y-4 py-4">
                    {messages.map((message) => (
                      <div
                        key={message.id}
                        className={`flex ${
                          message.senderId === user.id ? 'justify-end' : 'justify-start'
                        }`}
                      >
                        <div
                          className={`max-w-[70%] rounded-lg px-4 py-2 ${
                            message.senderId === user.id
                              ? 'bg-primary text-primary-foreground'
                              : 'bg-muted'
                          }`}
                        >
                          <p className="text-sm">{message.content}</p>
                          <p className={`text-xs mt-1 ${
                            message.senderId === user.id
                              ? 'text-primary-foreground/70'
                              : 'text-muted-foreground'
                          }`}>
                            {message.timestamp}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Message Input */}
                  <div className="border-t pt-4">
                    <div className="flex space-x-2">
                      <Input
                        placeholder="Type your message..."
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                        className="flex-1"
                      />
                      <Button onClick={sendMessage} disabled={!newMessage.trim()}>
                        <Send className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </>
            ) : (
              <CardContent className="flex items-center justify-center h-full">
                <div className="text-center">
                  <MessageCircle className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
                  <h3 className="text-lg font-medium mb-2">Select a conversation</h3>
                  <p className="text-muted-foreground">
                    Choose a chat from the list to start messaging
                  </p>
                </div>
              </CardContent>
            )}
          </Card>
        </div>
      </div>
    </div>
  )
}

app/doctors/page.tsx.content:
'use client'

import { useState, useEffect } from 'react'
import { useAuth } from '@/providers/auth-provider'
import { supabase } from '@/lib/supabase/client'
import { PageLoadingSpinner } from '@/components/loading-spinner'
import { Navbar } from '@/components/navbar'
import { BreadcrumbNav } from '@/components/breadcrumb-nav'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import { Stethoscope, Search, Calendar, Star, Clock, DollarSign } from 'lucide-react'
import { toast } from 'sonner'

interface Doctor {
  id: string
  user_id: string
  specialization?: string
  experience_years?: number
  consultation_fee?: number
  bio?: string
  availability_hours?: string
  created_at: string
  updated_at: string
  users?: {
    id: string
    full_name?: string
    email: string
  }
}

export default function DoctorsPage() {
  const { user, loading: authLoading } = useAuth()
  const [doctors, setDoctors] = useState<Doctor[]>([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState('')

  useEffect(() => {
    loadDoctors()
  }, [])

  const loadDoctors = async () => {
    try {
      const { data, error } = await supabase
        .from('doctors')
        .select(`
          *,
          users (
            id,
            full_name,
            email
          )
        `)
        .order('created_at')

      if (error) {
        console.error('Error loading doctors:', error)
        toast.error('Failed to load doctors')
      } else {
        setDoctors(data || [])
      }
    } catch (error) {
      console.error('Error:', error)
      toast.error('Failed to load doctors')
    } finally {
      setLoading(false)
    }
  }

  const bookAppointment = async (doctor: Doctor) => {
    if (!user) {
      toast.error('Please login to book an appointment')
      return
    }

    if (!isSupabaseConfigured()) {
      toast.error('Database not configured. Please set up Supabase.')
      return
    }

    try {
      // Create appointment for tomorrow at 10:00 AM (you can add date/time picker later)
      const tomorrow = new Date()
      tomorrow.setDate(tomorrow.getDate() + 1)

      const { error } = await supabase
        .from('appointments')
        .insert({
          patient_id: user.id,
          doctor_id: doctor.user_id,
          appointment_date: tomorrow.toISOString().split('T')[0],
          appointment_time: '10:00',
          status: 'pending',
          notes: 'General consultation'
        })

      if (error) throw error

      toast.success(`Appointment request sent to ${doctor.users?.full_name}. You will receive a confirmation shortly.`)
    } catch (error) {
      console.error('Error booking appointment:', error)
      toast.error('Failed to book appointment. Please try again.')
    }
  }

  const filteredDoctors = doctors.filter(doctor =>
    doctor.users?.full_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    doctor.specialization?.toLowerCase().includes(searchTerm.toLowerCase())
  )

  if (authLoading || loading) {
    return <PageLoadingSpinner />
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white">
      <Navbar />

      <div className="container py-8">
        <BreadcrumbNav items={[
          { label: 'Find Doctors' }
        ]} />

        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center space-x-3 mb-4">
            <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
              <Stethoscope className="h-6 w-6 text-blue-600" />
            </div>
            <div>
              <h1 className="text-3xl font-bold">Find Doctors</h1>
              <p className="text-muted-foreground">Book appointments with qualified specialists</p>
            </div>
          </div>

          {/* Search */}
          <div className="relative max-w-md">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search doctors or specializations..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>
        </div>

        {/* Doctors Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
          {filteredDoctors.map((doctor) => (
            <Card key={doctor.id} className="hover:shadow-lg transition-shadow">
              <CardHeader className="pb-3">
                <div className="w-20 h-20 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Stethoscope className="h-10 w-10 text-blue-600" />
                </div>
                <CardTitle className="text-xl text-center">{doctor.user?.full_name}</CardTitle>
                <CardDescription className="text-center">
                  {doctor.specialization}
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex items-center justify-between text-sm">
                    <div className="flex items-center space-x-2">
                      <Clock className="h-4 w-4 text-muted-foreground" />
                      <span>{doctor.experience_years} years exp.</span>
                    </div>
                    <div className="flex items-center space-x-2">
                      <DollarSign className="h-4 w-4 text-muted-foreground" />
                      <span>${doctor.consultation_fee}</span>
                    </div>
                  </div>

                  <div className="flex items-center space-x-2 text-sm">
                    <Star className="h-4 w-4 text-yellow-500" />
                    <span>4.8 (124 reviews)</span>
                  </div>

                  <p className="text-sm text-muted-foreground line-clamp-3">
                    {doctor.bio}
                  </p>

                  <div className="text-sm">
                    <span className="font-medium">Available:</span>
                    <p className="text-muted-foreground">{doctor.availability_hours}</p>
                  </div>

                  <Button 
                    onClick={() => bookAppointment(doctor)}
                    className="w-full"
                  >
                    <Calendar className="h-4 w-4 mr-2" />
                    Book Appointment
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {filteredDoctors.length === 0 && !loading && (
          <div className="text-center py-12">
            <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
              <Search className="h-8 w-8 text-muted-foreground" />
            </div>
            <h3 className="text-lg font-medium mb-2">No doctors found</h3>
            <p className="text-muted-foreground">
              Try adjusting your search terms
            </p>
          </div>
        )}
      </div>
    </div>
  )
}

app/pharmacy/page.tsx.content:
'use client'

import { useState, useEffect } from 'react'
import { useAuth } from '@/providers/auth-provider'
import { supabase, isSupabaseConfigured } from '@/lib/supabase/client'
import { PageLoadingSpinner } from '@/components/loading-spinner'
import { Navbar } from '@/components/navbar'
import { BreadcrumbNav } from '@/components/breadcrumb-nav'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import { Pill, Search, ShoppingCart, Plus, Minus } from 'lucide-react'
import { toast } from 'sonner'

interface Medicine {
  id: string
  name: string
  description?: string
  price: number
  stock_quantity: number
  category?: string
  image_url?: string
  created_at: string
  updated_at: string
}

interface CartItem extends Medicine {
  quantity: number
}

export default function PharmacyPage() {
  const { user, loading: authLoading } = useAuth()
  const [medicines, setMedicines] = useState<Medicine[]>([])
  const [cart, setCart] = useState<CartItem[]>([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState('')

  useEffect(() => {
    loadMedicines()
  }, [])

  const loadMedicines = async () => {
    try {
      if (!isSupabaseConfigured()) {
        console.warn('Supabase not configured. Please set up your environment variables.')
        setMedicines([])
      } else {
        const { data, error } = await supabase
          .from('medicines')
          .select('*')
          .order('name')

        if (error) {
          console.error('Error loading medicines:', error)
          toast.error('Failed to load medicines')
          setMedicines([])
        } else {
          setMedicines(data || [])
        }
      }
    } catch (error) {
      console.error('Error loading medicines:', error)
      toast.error('Failed to load medicines')
      setMedicines([])
    } finally {
      setLoading(false)
    }
  }

  const addToCart = (medicine: Medicine) => {
    if (!user) {
      toast.error('Please login to add items to cart')
      return
    }

    setCart(prev => {
      const existingItem = prev.find(item => item.id === medicine.id)
      if (existingItem) {
        return prev.map(item =>
          item.id === medicine.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        )
      }
      return [...prev, { ...medicine, quantity: 1 }]
    })
    toast.success(`${medicine.name} added to cart`)
  }

  const updateQuantity = (medicineId: string, newQuantity: number) => {
    if (newQuantity <= 0) {
      setCart(prev => prev.filter(item => item.id !== medicineId))
      return
    }

    setCart(prev =>
      prev.map(item =>
        item.id === medicineId
          ? { ...item, quantity: newQuantity }
          : item
      )
    )
  }

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0)
  }

  const getTotalItems = () => {
    return cart.reduce((total, item) => total + item.quantity, 0)
  }

  const handleCheckout = async () => {
    if (cart.length === 0) {
      toast.error('Your cart is empty')
      return
    }

    if (!isSupabaseConfigured()) {
      toast.error('Database not configured. Please set up Supabase.')
      return
    }

    try {
      // Create order
      const { data: order, error: orderError } = await supabase
        .from('orders')
        .insert({
          user_id: user!.id,
          total_amount: getTotalPrice(),
          status: 'pending',
          payment_method: 'cod',
          shipping_address: 'Default Address' // You can add address form later
        })
        .select()
        .single()

      if (orderError) throw orderError

      // Create order items
      const orderItems = cart.map(item => ({
        order_id: order.id,
        medicine_id: item.id,
        quantity: item.quantity,
        price: item.price
      }))

      const { error: itemsError } = await supabase
        .from('order_items')
        .insert(orderItems)

      if (itemsError) throw itemsError

      toast.success('Order placed successfully! You will receive a confirmation email shortly.')
      setCart([])
    } catch (error) {
      console.error('Error placing order:', error)
      toast.error('Failed to place order. Please try again.')
    }
  }

  const filteredMedicines = medicines.filter(medicine =>
    medicine.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    medicine.category?.toLowerCase().includes(searchTerm.toLowerCase())
  )

  if (authLoading || loading) {
    return <PageLoadingSpinner />
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-50 to-white">
      <Navbar />

      <div className="container py-8">
        <BreadcrumbNav items={[
          { label: 'Pharmacy' }
        ]} />

        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center space-x-3 mb-4">
            <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
              <Pill className="h-6 w-6 text-green-600" />
            </div>
            <div>
              <h1 className="text-3xl font-bold">Online Pharmacy</h1>
              <p className="text-muted-foreground">Order medicines with fast delivery</p>
            </div>
          </div>

          {/* Search and Cart */}
          <div className="flex flex-col sm:flex-row gap-4 items-center justify-between">
            <div className="relative flex-1 max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Search medicines..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
              />
            </div>

            {cart.length > 0 && (
              <div className="flex items-center space-x-2">
                <Button variant="outline" className="relative">
                  <ShoppingCart className="h-4 w-4 mr-2" />
                  Cart ({getTotalItems()})
                  <Badge className="ml-2">
                    ${getTotalPrice().toFixed(2)}
                  </Badge>
                </Button>
              </div>
            )}
          </div>
        </div>

        {/* Medicines Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 mb-8">
          {filteredMedicines.map((medicine) => {
            const cartItem = cart.find(item => item.id === medicine.id)

            return (
              <Card key={medicine.id} className="hover:shadow-lg transition-shadow">
                <CardHeader className="pb-3">
                  <div className="aspect-square bg-gray-100 rounded-lg mb-3 flex items-center justify-center">
                    <img 
                      src={medicine.image_url} 
                      alt={medicine.name}
                      className="w-full h-full object-cover rounded-lg"
                      onError={(e) => {
                        const target = e.target as HTMLImageElement
                        target.style.display = 'none'
                        target.nextElementSibling?.classList.remove('hidden')
                      }}
                    />
                    <Pill className="h-12 w-12 text-gray-400 hidden" />
                  </div>
                  <CardTitle className="text-lg line-clamp-2">{medicine.name}</CardTitle>
                  <CardDescription className="line-clamp-2">
                    {medicine.description}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    <div className="flex items-center justify-between">
                      <span className="text-2xl font-bold text-green-600">
                        ${medicine.price.toFixed(2)}
                      </span>
                      {medicine.category && (
                        <Badge variant="secondary">{medicine.category}</Badge>
                      )}
                    </div>

                    <div className="text-sm text-muted-foreground">
                      Stock: {medicine.stock_quantity} available
                    </div>

                    {cartItem ? (
                      <div className="flex items-center space-x-2">
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => updateQuantity(medicine.id, cartItem.quantity - 1)}
                        >
                          <Minus className="h-3 w-3" />
                        </Button>
                        <span className="px-3 py-1 bg-muted rounded text-sm font-medium">
                          {cartItem.quantity}
                        </span>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => updateQuantity(medicine.id, cartItem.quantity + 1)}
                          disabled={cartItem.quantity >= medicine.stock_quantity}
                        >
                          <Plus className="h-3 w-3" />
                        </Button>
                      </div>
                    ) : (
                      <Button
                        onClick={() => addToCart(medicine)}
                        disabled={medicine.stock_quantity === 0}
                        className="w-full"
                      >
                        {medicine.stock_quantity === 0 ? 'Out of Stock' : 'Add to Cart'}
                      </Button>
                    )}
                  </div>
                </CardContent>
              </Card>
            )
          })}
        </div>

        {filteredMedicines.length === 0 && !loading && (
          <div className="text-center py-12">
            <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
              <Search className="h-8 w-8 text-muted-foreground" />
            </div>
            <h3 className="text-lg font-medium mb-2">No medicines found</h3>
            <p className="text-muted-foreground">
              Try adjusting your search terms
            </p>
          </div>
        )}

        {/* Cart Summary */}
        {cart.length > 0 && (
          <Card className="sticky bottom-4">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="font-semibold">Cart Summary</h3>
                  <p className="text-sm text-muted-foreground">
                    {getTotalItems()} items ‚Ä¢ Total: ${getTotalPrice().toFixed(2)}
                  </p>
                </div>
                <Button size="lg" onClick={handleCheckout}>
                  Proceed to Checkout
                </Button>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  )
}

